import turtle


def function_1(eyes):
    eyes.penup()
    eyes.pencolor("black")
    eyes.fillcolor("white")
    for i in range(1):
        eyes.begin_fill()
        eyes.goto(-130,0)
        eyes.pendown()
        eyes.circle(130)
        eyes.end_fill()
        eyes.begin_fill()
        eyes.penup()
        eyes.goto(130,0)
        eyes.pendown()
        eyes.circle(130)
        eyes.end_fill()
        eyes.penup()
    for i in range(1):
        eyes.fillcolor("#45eafe")
        eyes.setpos(125,60)
        eyes.begin_fill()
        eyes.pendown()
        eyes.circle(60)
        eyes.end_fill()
        eyes.penup()
        eyes.setpos(-125,60)
        eyes.begin_fill()
        eyes.pendown()
        eyes.circle(60)
        eyes.end_fill()
        eyes.penup()
    for i in range(1):
        eyes.fillcolor("Black")
        eyes.setpos(125, 90)
        eyes.begin_fill()
        eyes.pendown()
        eyes.circle(30)
        eyes.end_fill()
        eyes.penup()
        eyes.setpos(-125, 90)
        eyes.begin_fill()
        eyes.pendown()
        eyes.circle(30)
        eyes.end_fill()
        eyes.penup()

def function_3(t):
    t.penup()
    t.speed(10)
    t.pencolor("black")
    t.fillcolor("white")
    for i in range(1):
        t.goto(0, -173)
        t.pendown()
        t.begin_fill()
        t.right(90)
        t.fd(50)
        t.left(90)
        t.fd(50)
        t.left(90)
        t.fd(50)
        t.right(90)
        t.backward(50)
        t.end_fill()
        t.penup()
    for i in range(1):
        t.goto(0, -173)
        t.pendown()
        t.begin_fill()
        t.left(90)
        t.backward(50)
        t.right(90)
        t.backward(50)
        t.right(90)
        t.backward(50)
        t.left(90)
        t.fd(50)
        t.end_fill()
        t.penup()

def function_2(line):
    line.penup()
    line.speed(10)
    line.pencolor("black")
    line.width(7)
    line.goto(-225,-50)
    line.pendown()
    line.setheading(-60)
    line.circle(250,120)
    line.penup()

    line.width(5)
    line.goto(260,-50)
    line.pendown()
    line.circle(30)
    line.penup()
    line.goto(240,-40)
    line.dot(10,"red"); line.goto(220,-40); line.dot(10,"red"); line.goto(230,-20);line.dot(10,"red")
    line.goto(-225,-50)
    line.pencolor("black")
    line.width(5)
    line.pendown()
    line.circle(30)
    line.penup()
    line.goto(-240, -40)
    line.dot(10, "red");line.goto(-260, -40);line.dot(10, "red");line.goto(-250, -20); line.dot(10, "red")
    line.penup()
    line.hideturtle()

def function_4(s):
    s.penup()
    s.speed(100)
    s.pencolor("green")
    s.fillcolor("#b3d02b")
    for i in range(1):
        s.goto(-300,-300)
        s.pendown()
        s.begin_fill()
        s.circle(50)
        s.end_fill()
        s.penup()
    for i in range(1):
        s.goto(300, 300)
        s.pendown()
        s.begin_fill()
        s.circle(60)
        s.end_fill()
        s.penup()
    for i in range(1):
        s.goto(-250, 250)
        s.pendown()
        s.begin_fill()
        s.circle(60)
        s.end_fill()
        s.penup()
    for i in range(1):
        s.goto(250, -250)
        s.pendown()
        s.begin_fill()
        s.circle(90)
        s.end_fill()
        s.penup()
    for i in range(1):
        s.goto(-270, 90)
        s.pendown()
        s.begin_fill()
        s.circle(40)
        s.end_fill()
        s.penup()
    for i in range(1):
        s.goto(-350,-60)
        s.pendown()
        s.begin_fill()
        s.circle(70)
        s.end_fill()
        s.penup()
    for i in range(1): #close to eyes
        s.goto(100, 270)
        s.pendown()
        s.begin_fill()
        s.circle(80)
        s.end_fill()
        s.penup()
    for i in range(1):
        s.goto(200,100)
        s.pendown()
        s.begin_fill()
        s.circle(90)
        s.end_fill()
        s.penup()
    for i in range(1):
        s.goto(-100, -300)
        s.pendown()
        s.begin_fill()
        s.circle(50)
        s.end_fill()
        s.penup()
    for i in range(1):
        s.goto(180, -300)
        s.pendown()
        s.begin_fill()
        s.circle(30)
        s.end_fill()
        s.penup()


