def function_7(text):
    text.penup()
    text.pendown()
    text.write("Bob Esponja", True, align="center",font=("times new roman",15,"bold"))
    text.hideturtle()
def main():
    wn = turtle.Screen()
    wn.bgcolor("yellow")
    eyes = turtle.Turtle()
    eyes.width(5)
    eyes.speed(100)
    line= turtle.Turtle()
    t= turtle.Turtle()
    s= turtle.Turtle()
    l= turtle.Turtle()
    r= turtle.Turtle()
    text= turtle.Turtle()
    function_4(s)
    function_6(r)
    function_5(l)
    function_1(eyes)
    function_3(t)
    function_2(line)
    function_7(text)





    wn.exitonclick()
main()


